import React from 'react'

import TodoItems from './TotoItems'
import TodoDetails from '../data/TodoDetails.js'

class Todos extends React.Component {
  constructor(){
    super()
    this.state = {
      todos: TodoDetails
    }
    this.handleOnChange = this.handleOnChange.bind(this)
  }
  handleOnChange(e){
    const id = e.target.id
    console.log('target Id', id)

    let state = this.state.todos.map(todo => {
        console.log('each id', todo.id)
          if(todo.id == id){
            console.log('from if')
            todo.isCompleted = !todo.isCompleted
          }
          return todo
    })
    console.log(state)
  }
  render(){
    const TodoList = this.state.todos.map(todos =>
      <TodoItems key={todos.id} todoItem={todos} handleOnChange={this.handleOnChange}/>);
    return (
      <div>
        {TodoList}
      </div>
    )
  }
}

export default Todos
